import jsonschema
import json

class User:
    schema = {
        "type" : "object",
        "properties" : {
            "firstName" : {
                "type" : "string"
            },
            "lastName" : {
                "type" : "string"
            },
            "age" : {
                "description" : "Age in years",
                "type" : "integer",
                "minimum" : 0
            }
        },
        "required" : ["firstName", "lastName"]
    }

    def __init__(self, firstName, lastName, age=None):
        self.firstName = firstName
        self.lastName = lastName
        self.age = age
        # validate the input JSON against the schema
        jsonschema.validate(self.toJson(), User.schema)

    def toJson(self):
        # returns a json representation of the user object.
        return json.dumps(self.__dict__)
